mapper.insert:
        INSERT INTO TB_USER(
               USER_ID
             , PASSWORD
		     , PHONE
             , USER_NAME
             , USER_TYPE
             , CREATED_TIME
        )
        VALUES (
               #{userId}
             , #{password}
		     , #{phone}
             , #{userName}
             , #{userType}
             , NOW()
        )

mapper.update:
        UPDATE TB_USER
           SET PASSWORD  = #{password}
		     , PHONE     = #{phone}
             , USER_NAME = #{userName}
             , USER_TYPE = #{userType}
         WHERE USER_ID   = #{userId}
